datalist = []items = []no  = int(input("\n Enter the number of elements in list :"))for i in range(no):     item = int(input("\n Enter the value of elements :"))     datalist.append(item)print("\n List before Reverse order of elements :",datalist)for i in range(len(datalist),0,-1):     items.append(datalist[i-1])print("\n Elements in Reverse order :",items)