datalist = []no = int(input("\n Enter the no of elments in list :"))for i in range(no):    values = int(input("\n Enter the vallues: "))    datalist.append(values)print("\n List before replace the element :", datalist)old_no = int(input("\n Enter old value which you want do replace :"))new_no = int(input("\n Enter the new value to replace :"))for n, i in enumerate(datalist):  # enumerate is used to fetch the value of list as wel as index of the list    if i == old_no:        datalist[n] = new_noprint("\n List after replace the element :", datalist)